import React, { Component } from 'react';

class Counter extends Component {
    // state = {
    //     value: this.props.counter.value,
    //     tags: ["Egg", "Chicken", "Milk"]
    // };

    // constructor() {
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this);
    // }

    styles = {
        fontSize: 50,
        fontWeight: "bold"
    }

    handleIncrement = product => {
        this.setState( {value : this.state.value + 1} )
    }

    render() { 
        return (
        <React.Fragment>
            {this.props.children}
            <span className = {this.getbadgeClasses()}>{this.formatcount()}</span>
        <button onClick = { product => this.handleIncrement(product)} className = 'btn btn-secondary btn-sm'>Increment</button>
        <button onClick = { () => this.props.onDelete(this.props.counter.id) } className = "btn btn-danger btm-sm m-2">Delete</button>
        {<ul>
           {/* {this.state.tags.map(tag => <li key = {tag}>{tag}</li>)} */}
        </ul>}
        </React.Fragment>);
    }

    getbadgeClasses() {
        let classes = "badge m-2 bg-";
        classes += this.state.value === 0 ? "warning" : "primary";
        return classes;
    }

    formatcount() {
        const { value: count } = this.state;
        const x = "Zero"
        return count === 0 ? x : count;
    }
}
export default Counter;
